--cream tabela utilizatori
DROP TABLE UTILIZATORI CASCADE CONSTRAINTS;
DROP TABLE FILME CASCADE CONSTRAINTS;
DROP TABLE REGIZORI CASCADE CONSTRAINTS;
DROP TABLE NOTA_FILM CASCADE CONSTRAINTS;
DROP TABLE RECENZII CASCADE CONSTRAINTS;
DROP TABLE PREMII CASCADE CONSTRAINTS;
DROP TABLE NOMINALIZARI CASCADE CONSTRAINTS;
DROP TABLE ACTORI CASCADE CONSTRAINTS;
DROP TABLE DISTRIBUTIE CASCADE CONSTRAINTS;

CREATE TABLE UTILIZATORI (
  ID INT NOT NULL PRIMARY KEY,
  Username VARCHAR2(30) NOT NULL,
  Parola VARCHAR2(15) NOT NULL,
  Gen VARCHAR2(15) NOT NULL,
  Data_nasterii DATE,
  Admin VARCHAR(3),
  Email VARCHAR2(40),
  Created_at DATE,
  Updated_at DATE
)
/ 
CREATE TABLE REGIZORI(
  ID INT NOT NULL PRIMARY KEY,
  Nume VARCHAR2(30),
  Prenume VARCHAR2(30),
  Data_nastere date,
  Gen VARCHAR2(10),
  Bio VARCHAR2(2000),
  Created_at date,
  Updated_at date
)
/
CREATE TABLE FILME(
  ID INT NOT NULL PRIMARY KEY,
  Nume VARCHAR2(30) NOT NULL,
  Descriere VARCHAR2(500) NOT NULL,
  Gen VARCHAR(10) NOT NULL,
  Tara_Origine VARCHAR(20) NOT NULL,
  Data_lansarii date,
  Id_regizor INT NOT NULL,
  Created_at date,
  Updated_at date,
  CONSTRAINT filme_Id_regizor FOREIGN KEY(Id_regizor) REFERENCES REGIZORI(ID)
)
/
CREATE TABLE ACTORI(
  ID INT NOT NULL PRIMARY KEY,
  Nume VARCHAR2(30) NOT NULL,
  Prenume VARCHAR2(30) NOT NULL,
  Gen VARCHAR2(10) NOT NULL,
  Data_nastere date NOT NULL,
  Bio VARCHAR2(2000) NOT NULL,
  Created_at date,
  Updated_at date
)
/
CREATE TABLE NOTA_FILM(
  ID INT NOT NULL PRIMARY KEY,
  Id_utilizator INT NOT NULL,
  Id_film INT NOT NULL,
  Valoare INT NOT NULL,
  Data_notarii date,
  Created_at date,
  Updated_at date,
  CONSTRAINT nota_film_utilizator FOREIGN KEY (Id_utilizator) REFERENCES UTILIZATORI(ID),
  CONSTRAINT nota_film_film_notat FOREIGN KEY (Id_film) REFERENCES FILME(ID)
)
/
CREATE TABLE RECENZII(
  ID INT NOT NULL PRIMARY KEY,
  Id_utilizator INT NOT NULL,
  Id_film INT NOT NULL,
  Recenzie VARCHAR(500) NOT NULL,
  Data_rencezie date,
  Created_at date,
  Updated_at date,
  CONSTRAINT recenzii_utilizator FOREIGN KEY (Id_utilizator) REFERENCES UTILIZATORI(ID),
  CONSTRAINT recenzii_film FOREIGN KEY (Id_film) REFERENCES FILME(ID)  
)
/
CREATE TABLE DISTRIBUTIE(
  ID INT NOT NULL PRIMARY KEY,
  Id_film INT NOT NULL,
  Id_actor INT NOT NULL,
  Create_at date,
  Updated_at date,
  CONSTRAINT distributie_film FOREIGN KEY (Id_film) REFERENCES FILME(ID),
  CONSTRAINT distributie_actor FOREIGN KEY (Id_actor) REFERENCES ACTORI(ID)
)
/
CREATE TABLE PREMII(
  ID INT NOT NULL PRIMARY KEY,
  Nume VARCHAR2(30) NOT NULL,
  Categoria VARCHAR2(100) NOT NULL,
  Created_at date,
  Updated_at date
)
/
CREATE TABLE FILME_CASTIGATOARE(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_film INT NOT NULL,
  Data_premiat date,
  Created_at date,
  Updated_at date,
  CONSTRAINT filme_castigatoare_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT filme_castigatoare_film FOREIGN KEY (Id_film) REFERENCES FILME(ID)
)
/
CREATE TABLE REGIZORI_CASTIGATOARI(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_regizor INT NOT NULL,
  Id_film INT NOT NULL,
  Data_premiat date,
  Created_at date,
  Updated_at date,
  CONSTRAINT regizoti_castigatori_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT regizori_castigatori_film FOREIGN KEY (Id_film) REFERENCES FILME(ID),
  CONSTRAINT regizori_castigatori_regizor FOREIGN KEY (Id_regizor) REFERENCES REGIZORI(ID)
)
/
CREATE TABLE ACTORI_CASTIGATOARI(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_actor INT NOT NULL,
  Id_film INT NOT NULL,
  Data_premiat date,
  Created_at date,
  Updated_at date,
  CONSTRAINT actori_castigatori_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT actori_castigatori_film FOREIGN KEY (Id_film) REFERENCES FILME(ID),
  CONSTRAINT actori_castigatori_actor FOREIGN KEY (Id_actor) REFERENCES ACTORI(ID)
)
/
CREATE TABLE NOMINALIZARI_FILME(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_film INT NOT NULL,
  Created_at date,
  Updated_at date,
  CONSTRAINT nominalizari_filme_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT nominalizari_filme_film FOREIGN KEY (Id_film) REFERENCES FILME(ID)
)
/
CREATE TABLE NOMINALIZARI_REGIZORI(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_regizor INT NOT NULL,
  Id_film INT NOT NULL,
  Created_at date,
  Updated_at date,
  CONSTRAINT nominalizari_regizori_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT nominalizari_regizori_film FOREIGN KEY (Id_film) REFERENCES FILME(ID),
  CONSTRAINT nominalizari_regizori_regizor FOREIGN KEY (Id_regizor) REFERENCES REGIZORI(ID)
)
/
CREATE TABLE NOMINALIZARI_ACTORI(
  ID INT NOT NULL PRIMARY KEY,
  Id_premiu INT NOT NULL,
  Id_actor INT NOT NULL,
  Id_film INT NOT NULL,
  Created_at date,
  Updated_at date,
  CONSTRAINT nominalizari_actori_premiu FOREIGN KEY (Id_premiu) REFERENCES PREMII(ID),
  CONSTRAINT nominalizari_actori_film FOREIGN KEY (Id_film) REFERENCES FILME(ID),
  CONSTRAINT nominalizari_actori_actor FOREIGN KEY (Id_actor) REFERENCES ACTORI(ID)
)
/
  
  
